const display=document.querySelector(".display"),buttons=document.querySelectorAll("button"),specialChars=["%","*","/","-","+","="];let output="";const calculate=btnValue=>{if(display.focus(),"="===btnValue&&""!==output)output=eval(output.replace("%","/100"));else if("AC"===btnValue)output="";else if("DEL"===btnValue)output=output.toString().slice(0,-1);else{if(""===output&&specialChars.includes(btnValue))return;output+=btnValue}display.value=output};buttons.forEach((t=>{t.addEventListener("click",(t=>calculate(t.target.dataset.value)))}));